@import "common.scss";


footer {
  position: relative;
  background-image: url("../img/footer-background.jpg");
  height: 230px;

  &::before {
    content: "";
    display: block;
    width: 100%;
    height: 210px;
    background-image: url("../img/footer-layer-01.png");
    position: absolute;
    top: -210px;
  }

  &::after {
    content: "";
    display: block;
    width: 100%;
    height: 210px;
    background-image: url("../img/footer-layer-02.png");
    position: absolute;
    top: -210px;
    z-index: -1;
    background-repeat: no-repeat;
  }

  .inner {
    max-width: $maxwidth;
    margin: 0 auto;

    div {
      color: #fff;
      &.top{
        width: 100%;
        display: flex;
        justify-content: space-between;
        border-bottom: 1px solid #7b7b7b;
        padding: 10px 0;
        ul {

          li {
            display: inline-block;
            &:nth-child(1):after,
            &:nth-child(2):after{
              content: "/";
              display: inline-block;
              margin: 0 10px;
              vertical-align: middle;
            }
            
            a {
              color: #fff;
              display: inline-block;
              padding: 10px 0;
              letter-spacing: 2px;
            }
          }
        }
        span.sns {
          a {
            color: #fff;
            display: inline-block;
            margin-left: 20px;
            i {
              font-size: 24px;
            }
          }
        }
      }
      &.bottom {
        width: 100%;
        display: flex;
        justify-content: space-between;
        padding: 20px 0;
        p {
          font-size: 11px;
          letter-spacing: 3px;
        }
        .site {
          width: 140px;
          border-bottom: 2px solid #ccc;
          padding: 15px 20px;
          box-sizing: border-box;
          position: relative;
          font-weight: 600;
          letter-spacing: 3px;
          font-size: 15px;
          position: relative;
          &::after {
            content: "\f107";
            display: inline-block;
            @include web_font;
            position: absolute;
            right: 20px;
            top: 50%;
            transform: translateY(-50%);
            font-size: 14px;
            transition: 0.5s;
          }
          &.current {
            background: #fff;
            color: #676767;
            &::after {
              transform: rotate(180deg);
              color: #676767;
              font-size: 12px;
              transform-origin: 40% 38%;
            }
          }
          &.current ul {
            display: block;
            border-top: 1px solid #676767;
          }
          ul {
            position: absolute;
            background: #fff;
            box-sizing: border-box;
            width: 100%;
            bottom: 0;
            left: 0;
            transform: translateY(100%);
            display: none;
            li {
              color:#676767;
              cursor: pointer;
              padding: 15px 20px;
              transition: 0.5s;
              &:first-child {
                color: $point;
              }
              &:hover {
                background: #eee;
              }
            }
          }
        }
      }
    }

  }
}
