        align-items: center;
        &:nth-child(2),
        &:nth-child(3){
          display: none;
        }
        img {
          height: 100%;
          margin-right: 20px;
          padding: 2%;
          box-sizing: border-box;   
        }
        span.wrap {
          text-align: center;
          &::before {
            content: '\f10d';
            margin-bottom: 10px;
            @include web_font;
         }
          h3 {
            font-size: 24px;
            font-weight: 400;
            margin-bottom: 20px;
          }
          p {
            line-height: 1.5;
            margin-bottom: 50px;
            &::after {
              content: '\f10e';
              margin-top: 10px;
              @include web_font;
           }
          }
          a {
            display: block;
            margin: 0 auto;
            text-align: left;
          }
        }

      }
    }
  }
  .slide_count {
    transform: translate(-100%, 0);
    span {

    }
  }
}

/* SCROLL TOP */
.scroll_top {
  width: 65px;
  height: 65px;
  display: block;
  background: transparent;
  border: 1px solid #ccc;
  border-radius: 50%;
  position: fixed;
  right: 20px;
  bottom: 20px;
  z-index: 3;
  cursor: pointer;
  &::before {
    content: "\f30c";
    @include web_font;
    transition: 0.5s;
  }
  &:hover::before {
    color: #007a88 !important;
  }
  &.fixed {
    position: absolute;
    bottom: 455px;
  }
  &.rotate::before {
    transform: rotate(180deg);
  }
