@media (max-width: 1600px) {
  header {
    .inner {
      padding: 40px 50px;

      h1 {
        transform: scale(1.1);
      }
    }
  }

  .h_font {
    font-size: 40px !important;
  }

  #best_sellers {
    .inner {
      max-width: 1200px;
    }
  }

  footer {
    .inner {
      max-width: 1200px;
    }
  }

  .scroll_top {
    width: 50px;
    height: 50px;
  }
}

@media (max-width: 1200px) {
  header {
    .inner {
      padding: 20px 50px;

      h1 {
        transform: scale(1);
      }
    }
  }

  #title {
    h3 {
      font-size: 46px !important;
    }
  }

  #best_sellers {
    ul {
      gap: 70px;

      li {
        padding: unset;
      }
    }
    h3 {
      font-size: 20px !important;
    }
  }

  #brand_story {
    div {
      height: 560px;

      video {
        width: auto;
        height: auto;
      }
    }

    h2 {
      font-size: 90px;
      top: 50px;
    }

    h3 {
      font-size: 30px !important;
    }
  }

  #experience {
    .inner {
      flex-wrap: wrap;
      padding-bottom: 100px;

      h2 {
        width: 100%;
        display: block;

        span {
          display: inline-block;
        }
      }

      ul {
        width: 100%;
        li{
          justify-content: center !important;
          span.wrap {
            padding-right: 100px;
            width: 50%;
          }
          img {
            height: 85% !important;
          }
        }
      }
    }
  }

  footer {
    .inner {
      padding: 0 50px;
    }
  }
}

@media (max-width: 798px) {
  .h_font {
    font-size: 30px !important;
  }
  #title {
    height: 450px;

    .title_slide {
      li {
        padding-left: 40px !important;

        h3 {
          font-size: 36px !important;
        }
        a.more::after {
          width: 30px;
          height: 30px;
        }
      }

    }
    .slide_count {
      width: 50px;
      height: 120px;
      span {
        font-size: 26px;
      }
    }
  }

  #best_sellers {
    .inner {
      padding: 80px 50px;
      h2 {
        margin-bottom: 0;
      }
      ul {
        grid-template-rows: repeat(3, 1fr);
        grid-template-columns: repeat(2, 1fr);
        gap: 40px 34px;
        margin-top: 60px !important;
        li {
          
          &:nth-child(2){
            margin-top: unset;
          }
          &:nth-child(5){
            margin-top: unset;
          }
          h3 {
            font-size: 16px !important;
            text-align: center;
          }
        }

      }
    }
  }
  #brand_story {

    h2 {
      display: none;
    }
    video {
      transform: translate(-40%, -10%);
    }
    .brand_slide {
      width: calc(100% * 3) !important;
      li {
        width: 100%; 
        padding: 0 50px !important;
      }
    } 
    .brand_slide_btn {
      width: 70% !important;
      padding-left: 50px !important;
    }
  }

  #brand_material {
    height: 900px !important;
    &::before {
      background-size: 140%;
      top:81%;
    }
    &::after {
      top: 55%;
      background-size: 70%;
    }
    .wrap {
      flex-direction: column;
      align-items: center;
      justify-content: flex-start;
      div {
        width: 100%;
        &.scent {
          margin-bottom: 50px;
          a::before {
            background-size: 40%;
            height: 180px !important;
          }
        }
        &.ingredients {
          background-size: 50%;
          height: 250px !important;
          a::before {
            background-size: 40%;
            height: 250px !important;
          }
        }
        h3 {
          font-size: 20px !important;
          margin-bottom: 20px !important;
        }
      }
    }
  }
  #experience {
    .inner{
      height: auto;
      ul {
        li {
          flex-direction: column;
          justify-content: inherit;
          img {
            width: 50%;
            height: auto !important;
            margin-right: initial;
          }
          span.wrap {
            padding-right: initial;
            width: 100%;
          }
        }
      }
      .slide_count {
        transform: translate(-100%, -100%);
      }
    }
    
  }
}

