            repeat: no-repeat;
            position: center;
          }
        }
        .wrap {
          width: 700px;
          margin: 0 auto;
          font-weight: 300;
          color:#474747;
        }
        h3 {
          font-size: 32px;
          letter-spacing: 8px;
          margin-bottom: 20px;
        }
        p {
          line-height: 1.5;
          margin-bottom: 50px;
        }
        a {
          color: #646464;
          line-height: 1.5;
          border-bottom: 1px solid #646464;
          transition: 0.5s;
          &:hover {
            color: #000;
          }
        }
      }
    }
    .slide_btn {
      position: absolute;
      display: flex;
      bottom: 10%;
      left: 50%;
      transform: translateX(-50%);
      li {
        width: 20px;
        height: 20px;
        margin: 7px;
        border-radius: 50%;
        box-sizing: border-box;
        cursor: pointer;
        position: relative;
        &::after {
          content: "";
          display: block;
          width: 7px;
          height: 7px;
          background: #333;
          border-radius: 50%;
          position: absolute;
          top: 50%;
          left: 50%;
          transform: translate(-50%, -50%);
        }
        &.active {
          border: 1px solid #333;
        }
      }
    }
  }
}
